options.css:
:root {
  --rain-primary: #F97316;   /* laranja destaque */
  --rain-neutral: #94A3B8;   /* cinza texto */
  --rain-bg-dark: #020617;   /* fundo escuro */
  --rain-bg-card: #020617;   /* base do card */
}

.header-container {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 100%;
  padding: 6px;
  box-sizing: border-box;
  background: transparent; /* painel transparente, quem manda é o fundo global */
}

.rect-container {
  width: 100%;
  height: 100%;
  padding: 10px 20px;
  border-radius: 18px;
  background:
    radial-gradient(circle at 10% 0%, rgba(249,115,22,0.28), transparent 55%),
    radial-gradient(circle at 90% 100%, rgba(148,163,184,0.20), transparent 55%),
    linear-gradient(135deg, #020617, #020617);
  border: 1px solid rgba(249,115,22,0.85);
  box-shadow:
    0 0 0 1px rgba(15,23,42,0.7),
    0 18px 40px rgba(0,0,0,0.85),
    0 0 32px rgba(15,23,42,0.85);
  display: flex;
  align-items: center;
  justify-content: center;
}

/* Grid: [logo esquerda] [texto central] [placeholder/segunda logo] */
.border-container {
  display: grid;
  grid-template-columns: auto 1fr auto;
  align-items: center;
  width: 100%;
  box-sizing: border-box;
  gap: 22px;
}

.logo {
  height: auto;
  display: block;
}

.logo-left {
  width: 130px;
  max-width: 16vw;
}

.logo-right {
  width: 130px;
  max-width: 16vw;
}

.right-placeholder {
  width: 150px;
  height: 64px;
  display: flex;
  align-items: center;
  justify-content: center;
}

/* Neon leve nas logos */
.neon-logo {
  filter:
    drop-shadow(0 0 4px rgba(249,115,22,0.85))   /* laranja */
    drop-shadow(0 0 14px rgba(249,115,22,0.55));
  transition: filter 0.25s ease, transform 0.25s ease;
}

.neon-logo:hover {
  filter:
    drop-shadow(0 0 8px rgba(249,150,72,1))
    drop-shadow(0 0 22px rgba(249,115,22,0.95));
  transform: translateY(-1px) scale(1.01);
}

.neon-logo-right {
  filter:
    drop-shadow(0 0 6px rgba(148,163,184,0.9))
    drop-shadow(0 0 18px rgba(148,163,184,0.7));
  transition: filter 0.25s ease, transform 0.25s ease;
}

.neon-logo-right:hover {
  filter:
    drop-shadow(0 0 10px rgba(226,232,240,1))
    drop-shadow(0 0 26px rgba(148,163,184,0.95));
  transform: translateY(-1px) scale(1.02);
}

.text-container {
  display: flex;
  flex-direction: column;
  gap: 4px;
  text-align: center;
  justify-self: center;
}

.title-text {
  color: #e2e8f0;
  font-family: "Segoe UI", system-ui, -apple-system, BlinkMacSystemFont, sans-serif;
  font-size: 24px;
  font-weight: 800;
  letter-spacing: 0.18em;
  text-transform: uppercase;
  margin: 0;
  text-shadow:
    0 0 10px rgba(15,23,42,0.9),
    0 0 22px rgba(0,0,0,0.8);
}

.subtitle-text {
  color: rgba(226,232,240,0.92);
  font-family: "Segoe UI", system-ui, -apple-system, BlinkMacSystemFont, sans-serif;
  font-size: 13px;
  letter-spacing: 0.08em;
  text-transform: uppercase;
  margin: 0;
  opacity: 0.9;
}

.float-all {
  animation: rain-float 4s ease-in-out infinite;
}

@keyframes rain-float {
  0%, 100% { transform: translateY(0px); }
  50%      { transform: translateY(-3px); }
}

@media (max-width: 1100px) {
  .logo-left, .logo-right { width: 110px; }
  .title-text  { font-size: 20px; letter-spacing: 0.14em; }
  .subtitle-text { font-size: 12px; }
}

@media (max-width: 780px) {
  .border-container {
    grid-template-columns: 1fr;
    grid-template-rows: auto auto auto;
    justify-items: center;
    gap: 12px;
  }
  .logo-left, .logo-right { width: 90px; }
  .right-placeholder { width: 120px; height: 52px; }
}

