options.onRender:
// Fundo global do dashboard para o projeto de chuva (laranja + cinza)
const doc = window.top.document;

// Remove fundo anterior, se existir
const oldBg = doc.getElementById('rain-global-bg');
if (oldBg) oldBg.remove();

const oldStyle = doc.getElementById('rain-global-bg-style');
if (oldStyle) oldStyle.remove();

// Cria o <style> com a animação e o estilo do fundo
const style = doc.createElement('style');
style.id = 'rain-global-bg-style';
style.textContent = `
  @keyframes rainGradientShift {
    0%   { background-position: 0% 50%; }
    50%  { background-position: 100% 50%; }
    100% { background-position: 0% 50%; }
  }

  #rain-global-bg {
    position: fixed;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
    z-index: -1;              /* atrás de tudo */
    pointer-events: none;     /* não bloqueia cliques */

    /* Fundo escuro com toques de laranja (#F97316) e cinza (#94A3B8) */
    background:
      radial-gradient(circle at 15% 0%, rgba(249,115,22,0.24), transparent 55%),
      radial-gradient(circle at 85% 100%, rgba(148,163,184,0.18), transparent 55%),
      linear-gradient(-45deg, #020617, #020617, #0B1220, #020617);
    background-size: 260% 260%;
    animation: rainGradientShift 26s ease-in-out infinite;
  }
`;
doc.head.appendChild(style);

// Cria o elemento de fundo global
const bg = doc.createElement('div');
bg.id = 'rain-global-bg';
doc.body.appendChild(bg);

options.getOption:
// =====================================
// Gráfico 2 - Donut por intensidade de chuva
// Mesma query do Gráfico 1 (Infinity CSV)
// =====================================

// Pega os frames de dados que o Business Chart recebe
const frames = (context.panel && context.panel.data && context.panel.data.series) || [];

// Se não vier nada, mostra mensagem amigável
if (!frames.length) {
  return {
    title: {
      text: 'Distribuição por intensidade de chuva (sem dados visíveis)',
      left: 'center',
      top: 16,
      textStyle: {
        fontSize: 16,
        fontWeight: 600,
        color: '#e2e8f0',
      },
    },
    backgroundColor: 'transparent',
    series: [],
  };
}

// Vamos usar o primeiro frame (tua query só tem 1)
const frame = frames[0];
const fields = frame.fields || [];

// Acha o índice da coluna PRECIPITACAO_TOTAL (case-insensitive)
const idxPrec = fields.findIndex(
  f => String(f.name).toUpperCase() === 'PRECIPITACAO_TOTAL'
);

if (idxPrec === -1) {
  return {
    title: {
      text: 'Coluna PRECIPITACAO_TOTAL não encontrada',
      left: 'center',
      top: 16,
      textStyle: {
        fontSize: 14,
        fontWeight: 600,
        color: '#f97316',
      },
    },
    backgroundColor: 'transparent',
    series: [],
  };
}

// Extrai os valores da coluna como array JS
const precField = fields[idxPrec];
const precVals = precField.values.toArray().map(v => {
  const n = Number(v);
  return Number.isFinite(n) ? n : 0;
});

// Buckets de intensidade (mesmo critério do gráfico 1)
const buckets = {
  'sem chuva': 0,
  'chuva fraca': 0,
  'chuva moderada': 0,
  'chuva forte': 0,
};

// Classifica cada registro
for (const p of precVals) {
  if (p === 0) {
    buckets['sem chuva']++;
  } else if (p <= 5) {
    buckets['chuva fraca']++;
  } else if (p <= 25) {
    buckets['chuva moderada']++;
  } else {
    buckets['chuva forte']++;
  }
}

// Monta vetores para o gráfico
const categorias = Object.keys(buckets);
const valores = categorias.map(c => buckets[c]);
const total = valores.reduce((a, b) => a + b, 0);

// Tema do Grafana
const theme = context.grafana.theme || { isDark: true };
const textColor = theme.isDark ? '#e2e8f0' : '#0f172a';

// Paleta fixa para não mudar a cada refresh
const COLOR_MAP = {
  'sem chuva': '#64748b',      // cinza
  'chuva fraca': '#38bdf8',    // azul claro
  'chuva moderada': '#22c55e', // verde
  'chuva forte': '#f97316',    // laranja bem destacado
};

// Dados para o donut
const seriesData = categorias.map(nome => ({
  name: nome,
  value: buckets[nome],
  itemStyle: {
    color: COLOR_MAP[nome] || '#64748b',
  },
}));

// =====================================
// Config ECharts - DONUT
// =====================================
return {
  backgroundColor: 'transparent',

  title: {
    text: 'Distribuição das intensidades de chuva',
    left: 'center',
    top: 16,
    textStyle: {
      fontSize: 16,
      fontWeight: 700,
      color: textColor,
      letterSpacing: 1.2,
    },
  },

  tooltip: {
    trigger: 'item',
    formatter: (p) => {
      const perc = total ? ((p.value / total) * 100).toFixed(2) : '0.00';
      return `${p.name}<br/>
              Registros: <b>${p.value}</b><br/>
              Percentual: <b>${perc}%</b>`;
    },
  },

  legend: {
    orient: 'vertical',
    left: 'left',
    top: 'middle',
    textStyle: {
      color: textColor,
    },
  },

  series: [
    {
      name: 'Intensidade de chuva',
      type: 'pie',
      radius: ['45%', '70%'],   // donut
      center: ['55%', '55%'],
      minAngle: 10,             // garante que segmentos minúsculos apareçam
      stillShowZeroSum: true,
      avoidLabelOverlap: true,
      data: seriesData,
      label: {
        show: true,
        formatter: (p) => {
          const percRaw = total ? (p.value / total) * 100 : 0;
          let percStr;

          if (percRaw > 0 && percRaw < 0.1) {
            percStr = '<0.1';
          } else {
            percStr = percRaw.toFixed(2);
          }

          return `${p.name}\n${percStr}%`;
        },
        color: textColor,
        fontSize: 11,
      },
      labelLine: {
        show: true,
        length: 12,
        length2: 10,
      },
      emphasis: {
        scale: true,
        scaleSize: 6,
        itemStyle: {
          shadowBlur: 16,
          shadowOffsetX: 0,
          shadowColor: 'rgba(15, 23, 42, 0.6)',
        },
        label: {
          fontSize: 13,
          fontWeight: 'bold',
        },
      },
    },
  ],
};

