options.getOption:
// =====================================
// Gráfico 3 - Intensidade de chuva por mês
// Barras empilhadas (sem chuva, fraca, moderada, forte)
// =====================================

const frames = (context.panel && context.panel.data && context.panel.data.series) || [];

if (!frames.length) {
  return {
    title: {
      text: 'Intensidade de chuva por mês (sem dados visíveis)',
      left: 'center',
      top: 16,
      textStyle: { fontSize: 16, fontWeight: 600, color: '#e2e8f0' },
    },
    backgroundColor: 'transparent',
    xAxis: { type: 'category', data: [] },
    yAxis: { type: 'value' },
    series: [],
  };
}

const frame = frames[0];
const fields = frame.fields || [];

// Localiza colunas
const idxData = fields.findIndex(f => String(f.name).toUpperCase() === 'DATA');
const idxPrec = fields.findIndex(f => String(f.name).toUpperCase() === 'PRECIPITACAO_TOTAL');

if (idxData === -1 || idxPrec === -1) {
  return {
    title: {
      text: 'Colunas DATA e/ou PRECIPITACAO_TOTAL não encontradas',
      left: 'center',
      top: 16,
      textStyle: { fontSize: 14, fontWeight: 600, color: '#f97316' },
    },
    backgroundColor: 'transparent',
    xAxis: { type: 'category', data: [] },
    yAxis: { type: 'value' },
    series: [],
  };
}

const dataField = fields[idxData];
const precField = fields[idxPrec];

const datas = dataField.values.toArray().map(String);
const precs = precField.values.toArray().map(v => {
  const n = Number(v);
  return Number.isFinite(n) ? n : 0;
});

// Classes de intensidade
const CLASSES = ['sem chuva', 'chuva fraca', 'chuva moderada', 'chuva forte'];

function classificaChuva(p) {
  if (p === 0) return 'sem chuva';
  if (p <= 5) return 'chuva fraca';
  if (p <= 25) return 'chuva moderada';
  return 'chuva forte';
}

// Agregação por mês
const byMonth = {};

for (let i = 0; i < datas.length; i++) {
  const dStr = datas[i]; // '2023-01-15'
  const p = precs[i];
  if (!dStr) continue;

  const mesKey = dStr.slice(0, 7); // 'YYYY-MM'
  const classe = classificaChuva(p);

  if (!byMonth[mesKey]) {
    byMonth[mesKey] = {};
    for (const c of CLASSES) byMonth[mesKey][c] = 0;
  }
  byMonth[mesKey][classe] += 1;
}

// Ordena meses cronologicamente
const mesesKeys = Object.keys(byMonth).sort();

// Rótulos amigáveis
const MESES_PT = ['Jan', 'Fev', 'Mar', 'Abr', 'Mai', 'Jun', 'Jul', 'Ago', 'Set', 'Out', 'Nov', 'Dez'];
const categoriasMes = mesesKeys.map(k => {
  const [y, m] = k.split('-').map(Number);
  const nomeMes = MESES_PT[(m - 1) % 12] || k;
  return `${nomeMes}/${String(y).slice(-2)}`;
});

// Monta séries
const seriesData = {};
for (const c of CLASSES) seriesData[c] = [];

for (const mes of mesesKeys) {
  const bucket = byMonth[mes];
  for (const c of CLASSES) {
    seriesData[c].push(bucket[c] || 0);
  }
}

// Tema / cores
const theme = context.grafana.theme || { isDark: true };
const textColor = theme.isDark ? '#e2e8f0' : '#0f172a';

const COLOR_MAP = {
  'sem chuva': '#64748b',
  'chuva fraca': '#38bdf8',
  'chuva moderada': '#22c55e', // verde
  'chuva forte': '#f97316',    // laranja
};

const rgba = (hex, a) => {
  const r = parseInt(hex.slice(1, 3), 16);
  const g = parseInt(hex.slice(3, 5), 16);
  const b = parseInt(hex.slice(5, 7), 16);
  return `rgba(${r}, ${g}, ${b}, ${a})`;
};

const gridColor = rgba('#94a3b8', 0.4);

// Config ECharts
return {
  backgroundColor: 'transparent',

  title: {
    text: 'Intensidade de chuva por mês (registros empilhados)',
    left: 'center',
    top: 16,
    textStyle: {
      fontSize: 16,
      fontWeight: 700,
      color: textColor,
      letterSpacing: 1.2,
    },
  },

  tooltip: {
    trigger: 'axis',
    axisPointer: { type: 'shadow' },
    formatter: (params) => {
      const mes = params[0].axisValue;
      let total = 0;
      params.forEach(p => { total += p.value; });

      const linhas = params.map(p => {
        const perc = total ? ((p.value / total) * 100).toFixed(1) : '0.0';
        return `${p.marker} ${p.seriesName}: <b>${p.value}</b> (${perc}%)`;
      }).join('<br/>');

      return `<b>${mes}</b><br/>${linhas}<br/>Total: <b>${total}</b>`;
    },
  },

  legend: {
    top: 40,
    left: 'center',
    textStyle: { color: textColor },
  },

  grid: {
    left: 64,
    right: 24,
    top: 80,
    bottom: 48,
    containLabel: true,
  },

  xAxis: {
    type: 'category',
    data: categoriasMes,
    axisLine: { lineStyle: { color: gridColor } },
    axisTick: { show: false },
    axisLabel: { color: textColor, fontSize: 11 },
  },

  yAxis: {
    type: 'value',
    name: 'Quantidade de registros',
    nameLocation: 'middle',
    nameGap: 50,
    axisLabel: { color: textColor },
    splitLine: { lineStyle: { color: gridColor } },
  },

  series: CLASSES.map(classe => {
    const isModerada = classe === 'chuva moderada';
    const isForte = classe === 'chuva forte';

    const baseSeries = {
      name: classe,
      type: 'bar',
      stack: 'total',
      emphasis: { focus: 'series' },
      itemStyle: {
        color: COLOR_MAP[classe] || '#64748b',
        borderColor: isModerada ? '#15803d'
          : isForte ? '#c2410c'
            : undefined,
        borderWidth: (isModerada || isForte) ? 1 : 0,
      },
      // 🔹 chuva moderada e forte ganham altura mínima
      barMinHeight: (isModerada || isForte) ? 4 : 0,
    };

    // 🔹 Esconder totalmente meses com 0 para moderada e forte
    if (isModerada || isForte) {
      baseSeries.data = seriesData[classe].map(v =>
        v === 0
          ? { value: 0, itemStyle: { opacity: 0 } } // invisível quando não houver registro
