/*  
  Histograma robusto da amplitude térmica diária (P95 – P10)
  - Bins (faixas) gerados dinamicamente com base no min/max das amplitudes
*/

const frames = (context.panel && context.panel.data && context.panel.data.series) || [];

if (!frames.length) {
  return {
    title: {
      text: 'Histograma robusto da amplitude térmica diária (sem dados)',
      left: 'center',
      top: 16,
      textStyle: { color: '#e2e8f0', fontSize: 16, fontWeight: 600 },
    },
    backgroundColor: 'transparent',
    xAxis: { type: 'category', data: [] },
    yAxis: { type: 'value' },
    series: [],
  };
}

const frame = frames[0];
const fields = frame.fields || [];

const idxData = fields.findIndex(f => String(f.name).toUpperCase() === 'DATA');
const idxTemp = fields.findIndex(f => String(f.name).toUpperCase() === 'TEMPERATURA_BULBO_SECO');

if (idxData === -1 || idxTemp === -1) {
  return {
    title: {
      text: 'Colunas DATA e/ou TEMPERATURA_BULBO_SECO não encontradas',
      left: 'center',
      top: 16,
      textStyle: { color: '#f97316', fontSize: 14, fontWeight: 600 },
    },
    backgroundColor: 'transparent',
    xAxis: { type: 'category', data: [] },
    yAxis: { type: 'value' },
    series: [],
  };
}

const datas = fields[idxData].values.toArray().map(String);
const temps = fields[idxTemp].values.toArray().map(v => {
  const n = Number(v);
  return Number.isFinite(n) ? n : NaN;
});

// --- agrupa temperaturas por dia ---
const porDia = {}; // { '2023-01-01': [22.3, 24.1, ...] }

for (let i = 0; i < datas.length; i++) {
  const d = datas[i];
  const t = temps[i];

  if (!d || !Number.isFinite(t)) continue;

  const dia = d.slice(0, 10); // 'YYYY-MM-DD'
  if (!porDia[dia]) porDia[dia] = [];
  porDia[dia].push(t);
}

// --- função de quantil ---
function quantile(sorted, q) {
  if (!sorted.length) return NaN;
  const pos = (sorted.length - 1) * q;
  const base = Math.floor(pos);
  const rest = pos - base;
  const v0 = sorted[base];
  const v1 = sorted[base + 1];
  if (v1 !== undefined) {
    return v0 + rest * (v1 - v0);
  }
  return v0;
}

// --- calcula amplitude robusta P95–P10 por dia ---
const amplitudes = [];
const dias = Object.keys(porDia).sort();

for (const dia of dias) {
  const arr = porDia[dia].slice().filter(Number.isFinite);
  if (arr.length < 12) continue; // exige nº mínimo de leituras para robustez

  arr.sort((a, b) => a - b);
  const p10 = quantile(arr, 0.10);
  const p95 = quantile(arr, 0.95);
  const amp = p95 - p10;

  if (Number.isFinite(amp) && amp >= 0) {
    amplitudes.push(amp);
  }
}

if (!amplitudes.length) {
  return {
    title: {
      text: 'Histograma robusto da amplitude térmica diária (sem amplitudes válidas)',
      left: 'center',
      top: 16,
      textStyle: { color: '#e2e8f0', fontSize: 16, fontWeight: 600 },
    },
    backgroundColor: 'transparent',
    xAxis: { type: 'category', data: [] },
    yAxis: { type: 'value' },
    series: [],
  };
}

// ===============================
//  Bins dinâmicos
// ===============================
let minAmp = Math.min(...amplitudes);
let maxAmp = Math.max(...amplitudes);

// arredonda para valores "bonitos"
minAmp = Math.floor(minAmp);
maxAmp = Math.ceil(maxAmp);

// evita casos degenerados
if (maxAmp <= minAmp) {
  maxAmp = minAmp + 1;
}

const numBins = 8; // quantidade de faixas
const step = (maxAmp - minAmp) / numBins;

const edges = [];
for (let i = 0; i < numBins; i++) {
  edges.push(minAmp + i * step);
}

const labels = [];
for (let i = 0; i < edges.length; i++) {
  const start = edges[i];
  const end = (i < edges.length - 1) ? edges[i + 1] : null;
  if (end !== null) {
    labels.push(`${start.toFixed(1)}–${end.toFixed(1)}°C`);
  } else {
    labels.push(`≥${start.toFixed(1)}°C`);
  }
}

// conta quantos dias caem em cada faixa
const contagens = new Array(labels.length).fill(0);

for (const a of amplitudes) {
  let idx = edges.findIndex((edge, i) => {
    const next = edges[i + 1];
    if (next === undefined) return false;
    return a >= edge && a < next;
  });

  if (idx === -1) {
    idx = labels.length - 1; // último bin (≥ último edge)
  }

  contagens[idx]++;
}

// ===============================
//  Visual
// ===============================
const theme = context.grafana.theme || { isDark: true };
const textColor = theme.isDark ? '#e2e8f0' : '#0f172a';

const rgba = (hex, a) => {
  const r = parseInt(hex.slice(1, 3), 16);
