// ===========================================================
// HEATMAP - PRESSÃO ATMOSFÉRICA (hora × mês)
// Versão corrigida: eixo Y com índice 0-based e visualMap dinâmico
// ===========================================================

const frames = (context.panel?.data?.series) || [];

if (!frames.length) {
  return {
    title: {
      text: 'Heatmap de Pressão (sem dados)',
      left: 'center',
      top: 16,
      textStyle: { color: '#e2e8f0' }
    },
    backgroundColor: 'transparent',
    series: []
  };
}

const frame = frames[0];
const fields = frame.fields || [];

// Localiza colunas
const idxData = fields.findIndex(f => f.name.toUpperCase() === 'DATA');
const idxHora = fields.findIndex(f => f.name.toUpperCase() === 'HORA_UTC');
const idxPress = fields.findIndex(f => f.name.toUpperCase() === 'PRESSAO_ATM_EST');

if (idxData === -1 || idxHora === -1 || idxPress === -1) {
  return {
    title: {
      text: 'Colunas DATA, HORA_UTC ou PRESSAO_ATM_EST não encontradas',
      left: 'center',
      top: 16,
      textStyle: { color: '#f97316' }
    },
    backgroundColor: 'transparent',
    series: []
  };
}

const datas = fields[idxData].values.toArray();
const horas = fields[idxHora].values.toArray();
const press = fields[idxPress].values.toArray().map(Number);

// Converte "1300 UTC" → 13
function parseHora(h) {
  if (!h) return null;
  const num = parseInt(String(h).slice(0, 2), 10);
  return Number.isFinite(num) ? num : null;
}

// Converte DATA → mês (1..12)
function parseMes(d) {
  if (!d) return null;
  const m = new Date(d).getMonth() + 1;
  return Number.isFinite(m) ? m : null;
}

// Acúmulo: matriz[mesIndex][hora] = [valores...]
const matriz = {};

for (let i = 0; i < datas.length; i++) {
  const mes = parseMes(datas[i]);   // 1..12
  const hora = parseHora(horas[i]); // 0..23
  const p = press[i];

  if (!Number.isFinite(p) || mes == null || hora == null) continue;

  const mesIndex = mes - 1; // 0..11 (agora bate com o eixo category)

  if (!matriz[mesIndex]) matriz[mesIndex] = {};
  if (!matriz[mesIndex][hora]) matriz[mesIndex][hora] = [];
  matriz[mesIndex][hora].push(p);
}

// Gera dados no formato [xIndex, yIndex, valor]
// x = hora (0..23), y = mesIndex (0..11)
const heatData = [];
let minVal = Infinity;
let maxVal = -Infinity;

for (let mesIndex = 0; mesIndex < 12; mesIndex++) {
  for (let hora = 0; hora < 24; hora++) {
    const arr = matriz[mesIndex]?.[hora] || [];
    if (!arr.length) {
      heatData.push([hora, mesIndex, null]);
      continue;
    }
    const media = arr.reduce((a, b) => a + b, 0) / arr.length;
    heatData.push([hora, mesIndex, media]);

    if (Number.isFinite(media)) {
      minVal = Math.min(minVal, media);
      maxVal = Math.max(maxVal, media);
    }
  }
}

// Se der algum problema, coloca defaults
if (!Number.isFinite(minVal) || !Number.isFinite(maxVal)) {
  minVal = 930;
  maxVal = 950;
}

// Tema do Grafana
const theme = context.grafana.theme || { isDark: true };
const textColor = theme.isDark ? '#e2e8f0' : '#0f172a';

return {
  backgroundColor: 'transparent',

  title: {
    text: 'Heatmap da pressão atmosférica (hora × mês)',
    left: 'center',
    top: 16,
    textStyle: { color: textColor, fontSize: 16, fontWeight: 700 }
  },

  tooltip: {
    position: 'top',
    formatter: (p) => {
      const hora = p.value[0];
      const mesIndex = p.value[1];
      const val = p.value[2];
      return `Mês: <b>${mesIndex + 1}</b><br/>
              Hora: <b>${hora}h</b><br/>
              Pressão média: <b>${val != null ? val.toFixed(1) : 'N/A'}</b>`;
    }
  },

  grid: { top: 80, left: 80, right: 40, bottom: 80 },

  xAxis: {
    type: 'category',
    data: Array.from({ length: 24 }, (_, i) => `${i}h`),
    axisLabel: { color: textColor }
  },

  yAxis: {
    type: 'category',
    data: Array.from({ length: 12 }, (_, i) => `Mês ${i + 1}`),
    axisLabel: { color: textColor }
  },

  visualMap: {
    min: minVal,
    max: maxVal,
    calculable: true,
    orient: 'horizontal',
    left: 'center',
    bottom: 24,
    textStyle: { color: textColor },
    inRange: {
      // azul → verde → laranja (baixa → média → alta pressão)
      color: ['#0ea5e9', '#22c55e', '#f97316']
    }
  },

  series: [
    {
      type: 'heatmap',
      data: heatData,
      label: { show: false }
    }
  ]
};

